import React, { useEffect, useState, useRef } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import axios from "axios";

// Ïª®ÌÖåÏù¥ÎÑà Ïä§ÌÉÄÏùº
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  height: 100%;
  background-color: black;
  padding: 0;
  box-sizing: border-box;
  cursor: none;
`;

// Í≤ÄÏÉâÎ∞î Ïª®ÌÖåÏù¥ÎÑà Ïä§ÌÉÄÏùº
const SearchContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  max-width: 1200px;
  margin-top: 20px;
  margin-bottom: 40px;
`;

// Í≤ÄÏÉâ ÏûÖÎ†• Ïä§ÌÉÄÏùº
const SearchInput = styled.input`
  flex: 1;
  height: 30px;
  border: 1px solid white;
  background-color: black;
  color: white;
  outline: none;
  font-size: 16px;
  margin-right: 10px;
  padding-left: 10px;
`;

// Í≤ÄÏÉâ Î≤ÑÌäº Ïä§ÌÉÄÏùº
const SearchButton = styled.button`
  width: 70px;
  height: 30px;
  border: 1px solid white;
  background-color: white;
  color: black;
  font-size: 16px;
  cursor: pointer;
  transition: background-color 0.3s;
`;

// Ï¢åÏö∞ Ïä§ÌÅ¨Î°§ Î≤ÑÌäº Ïä§ÌÉÄÏùº
const ScrollButton = styled.button`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background-color: rgba(0, 0, 0, 0.5);
  color: white;
  border: none;
  padding: 10px;
  cursor: pointer;
  z-index: 10;

  &:hover {
    background-color: rgba(0, 0, 0, 0.8);
  }
`;

const LeftButton = styled(ScrollButton)`
  left: 300px;
`;

const RightButton = styled(ScrollButton)`
  right: 300px;
`;

// Í∞ïÏùò Î¶¨Ïä§Ìä∏ Ïä§ÌÉÄÏùº
const LectureList = styled.div`
  width: 100%;
  max-width: 1200px;
  display: flex;
  overflow-x: hidden;
  padding: 20px 0;
  gap: 20px;
  margin: 0 auto;
  border-radius: 15px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);

  &::-webkit-scrollbar {
    height: 6px;
    border-radius: 50px;
    // border: 1px solid #fff;
  }

  &::-webkit-scrollbar-thumb {
    background: #561689;
    border-radius: 10px;
    // border: 1px solid #fff;
  }
`;

// Í∞ïÏùò Ìï≠Î™© Ïä§ÌÉÄÏùº
const LectureItem = styled.div`
  position: relative;
  background-size: cover;
  background-position: center;
  background-image: url(${(props) => props.imageUrl});
  color: black;
  padding: 20px;
  font-size: 18px;
  cursor: pointer;
  width: 400px;
  height: 600px;
  box-sizing: border-box;
  transition: transform 0.2s ease, background-color 0.2s ease;
  scroll-snap-align: center;
  flex-shrink: 0;
  margin-right: 20px;
  overflow: hidden;

  &:hover {
    transform: scale(1.05);
    z-index: 1;
  }

  &:hover::after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    z-index: 0;
  }
`;

const LectureTitle = styled.span`
  font-weight: bold;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  position: relative;
  z-index: 1;
  color: white;
  text-shadow: 0px 3px 3px rgba(255, 255, 255, 0.5);
`;

const InfoText = styled.div`
  font-size: 16px;
  color: white;
  margin-top: 10px;
  position: relative;
  z-index: 1;
  transform: translateX(-100%);
  transition: transform 0.5s ease, opacity 0.5s ease;
  opacity: 0;

  ${LectureItem}:hover & {
    transform: translateX(0);
    opacity: 1;
  }
`;

const Logo = styled.div`
  width: 100px;
  height: 100px;
  margin: 50px 0;
`;

export function LectureSearch() {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");
  const [lectures, setLectures] = useState([]);
  const searchRef = useRef(null);
  const lectureListRef = useRef(null);
  const scrollAnimationRef = useRef(null);

  // Ïù¥Î™®Ìã∞ÏΩòÏùò ÏúÑÏπòÎ•º ÏÉÅÌÉúÎ°ú Í¥ÄÎ¶¨
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    // ÎßàÏö∞Ïä§ Ïù¥Îèô Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Îì±Î°ù
    const handleMouseMove = (e) => {
      setCursorPosition({ x: e.pageX + 4, y: e.pageY + 4 });
    };
    document.addEventListener("mousemove", handleMouseMove);

    // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Ï†úÍ±∞
    return () => {
      document.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  // Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Îäî Ìï®Ïàò
  const fetchLectures = async () => {
    try {
      const response = await axios.get("/api/findAllLecture");
      setLectures(response.data);
    } catch (error) {
      console.error("Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïò§Î•ò: ", error);
    }
  };

  // Í≤ÄÏÉâ Ìï∏Îì§Îü¨
  const handleSearch = async () => {
    if (searchQuery.trim() === "") {
      alert("Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî.");
      return;
    }

    try {
      const response = await axios.get(
        "http://localhost:8080/api/searchLecture",
        {
          params: { query: searchQuery },
        }
      );

      if (response.data.length > 0) {
        setLectures(response.data);
      } else {
        alert("Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.");
        fetchLectures(); // Í∏∞Î≥∏ Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
      }
    } catch (error) {
      console.error("Í≤ÄÏÉâ Ï§ë Ïò§Î•ò Î∞úÏÉù: ", error);
      alert("Í≤ÄÏÉâ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      fetchLectures();
    }
  };

  // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä Ï≤òÏùå Î†åÎçîÎßÅÎê† Îïå Í∏∞Î≥∏ Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    fetchLectures();
  }, []);

  // Í∞ïÏùò ÌÅ¥Î¶≠ Ïãú Ïù¥Îèô
  const handleLectureClick = (id) => {
    navigate(`/lecture/${id}`);
  };

  // ÏóîÌÑ∞ ÌÇ§ ÏûÖÎ†• Í∞êÏßÄ Ìï∏Îì§Îü¨
  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSearch();
    }
  };

  // Î°úÍ≥† ÌÅ¥Î¶≠ Ïãú Ï¥àÍ∏∞Ìôî Î∞è Ïä§ÌÅ¨Î°§ Ïù¥Îèô
  const handleLogoClick = () => {
    fetchLectures(); // Í∏∞Î≥∏ Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
    setSearchQuery(""); // Í≤ÄÏÉâÏñ¥ Ï¥àÍ∏∞Ìôî
    if (searchRef.current) {
      searchRef.current.scrollIntoView({ behavior: "smooth" }); // Í≤ÄÏÉâÏ∞ΩÏúºÎ°ú Ïä§ÌÅ¨Î°§ Ïù¥Îèô
    }
  };

  const scroll = (direction) => {
    if (lectureListRef.current) {
      lectureListRef.current.scrollBy({ left: direction, behavior: "smooth" });
    }
  };

  // Î∂ÄÎìúÎüΩÍ≤å ÏôºÏ™ΩÏúºÎ°ú Ïä§ÌÅ¨Î°§ÌïòÎäî Ìï®Ïàò
  const startScrollLeft = () => {
    scrollSmoothly(-5); // ÏôºÏ™ΩÏúºÎ°ú Î∂ÄÎìúÎüΩÍ≤å Ïù¥Îèô (Í∞íÏùÑ Ï°∞Ï†ïÌï¥ÏÑú ÏÜçÎèÑÎ•º Ï†úÏñ¥)
  };

  // Î∂ÄÎìúÎüΩÍ≤å Ïò§Î•∏Ï™ΩÏúºÎ°ú Ïä§ÌÅ¨Î°§ÌïòÎäî Ìï®Ïàò
  const startScrollRight = () => {
    scrollSmoothly(5); // Ïò§Î•∏Ï™ΩÏúºÎ°ú Î∂ÄÎìúÎüΩÍ≤å Ïù¥Îèô
  };

  // requestAnimationFrameÏùÑ ÏÇ¨Ïö©Ìï¥ Î∂ÄÎìúÎüΩÍ≤å Ïä§ÌÅ¨Î°§
  const scrollSmoothly = (step) => {
    if (lectureListRef.current) {
      lectureListRef.current.scrollBy({ left: step });
      scrollAnimationRef.current = requestAnimationFrame(() =>
        scrollSmoothly(step)
      );
    }
  };

  // Ïä§ÌÅ¨Î°§ÏùÑ Î©àÏ∂îÎäî Ìï®Ïàò
  const stopScroll = () => {
    if (scrollAnimationRef.current) {
      cancelAnimationFrame(scrollAnimationRef.current);
    }
  };

  return (
    <Container>
      <SearchContainer ref={searchRef}>
        <SearchInput
          type="text"
          placeholder="LECTURE SEARCH"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <SearchButton onClick={handleSearch}>üîó</SearchButton>
      </SearchContainer>
      <LeftButton
        onMouseDown={startScrollLeft}
        onMouseUp={stopScroll}
        onMouseLeave={stopScroll}
      >
        ‚óÅ
      </LeftButton>
      <LectureList ref={lectureListRef}>
        {lectures.map((lecture) => (
          <LectureItem
            key={lecture.id}
            imageUrl={lecture.thumbnail}
            onClick={() => handleLectureClick(lecture.id)}
          >
            <LectureTitle>{lecture.lectureName}</LectureTitle>
            <InfoText>{lecture.description}</InfoText>
          </LectureItem>
        ))}
      </LectureList>
      <RightButton
        onMouseDown={startScrollRight}
        onMouseUp={stopScroll}
        onMouseLeave={stopScroll}
      >
        ‚ñ∑
      </RightButton>
      <Logo>
        <a href="#" onClick={handleLogoClick}>
          <img
            src="./images/logo/logo_white.png"
            width="100"
            height="100"
            alt="untitled_logo"
          />
        </a>
      </Logo>
      <div
        style={{
          position: "absolute",
          left: `${cursorPosition.x}px`,
          top: `${cursorPosition.y}px`,
          pointerEvents: "none",
          zIndex: 1000,
          fontSize: "24px",
        }}
      >
        üëÄ
      </div>
    </Container>
  );
}
